import React, { useState, useEffect } from 'react';
import './App.css';
import Solflare from '@solflare-wallet/sdk';

function App() {
  const [userAddress, setUserAddress] = useState('');
  const [heliusApiKey, setHeliusApiKey] = useState('');
  const [status, setStatus] = useState({ message: 'Please connect your wallet to continue...', type: 'info' });
  const [showVerification, setShowVerification] = useState(false);
  const [showNFTs, setShowNFTs] = useState(false);
  const [nfts, setNfts] = useState([]);
  const [welcomeMessage, setWelcomeMessage] = useState('Welcome to Meta Betties Private Key. Please verify your Wallet here');

  const API_BASE_URL = 'https://api-server-9zua.onrender.com'; // Use localhost for local development
  const tgId = new URLSearchParams(window.location.search).get('tg_id');

  useEffect(() => {
    loadConfig();
    if (!tgId) {
      updateStatus('Missing Telegram ID parameter!', 'error');
    }
  }, [tgId]); // eslint-disable-line react-hooks/exhaustive-deps

  const loadConfig = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/config`);
      const config = await response.json();
      setHeliusApiKey(config.helius_api_key);
      if (!config.helius_api_key) {
        updateStatus('API configuration not found. Please check server setup.', 'error');
      }
    } catch (error) {
      updateStatus('Failed to load configuration: ' + error.message, 'error');
    }
  };

  const updateStatus = (message, type = 'info') => {
    setStatus({ message, type });
  };

  const connectPhantom = async () => {
    try {
      if (typeof window.solana === 'undefined') {
        updateStatus('Phantom extension not found. Please install it from https://phantom.app.', 'error');
        return;
      }

      if (!window.solana.isPhantom) {
        updateStatus('Phantom extension not found. Please install it from https://phantom.app.', 'error');
        return;
      }

      const resp = await window.solana.connect();
      setUserAddress(resp.publicKey.toString());
      showVerificationSection();
    } catch (err) {
      updateStatus('Phantom connection failed: ' + err.message, 'error');
    }
  };

  const connectSolflare = async () => {
    try {
      const wallet = new Solflare();

      wallet.on('connect', () => {
        console.log('connected', wallet.publicKey.toString());
      });

      wallet.on('disconnect', () => {
        console.log('disconnected');
      });

      await wallet.connect();

      if (wallet.isConnected) {
        setUserAddress(wallet.publicKey.toString());
        showVerificationSection();
      } else {
        updateStatus('Solflare connection failed.', 'error');
      }
    } catch (err) {
      updateStatus('Solflare connection failed: ' + err.message, 'error');
    }
  };

  const connectBackpack = async () => {
    try {
      if (typeof window.xnft === 'undefined') {
        updateStatus('Backpack extension not found. Please install it from https://backpack.app.', 'error');
        return;
      }

      if (!window.xnft.solana) {
        updateStatus('Backpack extension not found. Please install it from https://backpack.app.', 'error');
        return;
      }

      const resp = await window.xnft.solana.connect();
      setUserAddress(resp.publicKey.toString());
      showVerificationSection();
    } catch (err) {
      updateStatus('Backpack connection failed: ' + err.message, 'error');
    }
  };

  const connectSlope = async () => {
    try {
      if (typeof window.slope === 'undefined') {
        updateStatus('Slope extension not found. Please install it from https://slope.finance.', 'error');
        return;
      }

      if (!window.slope.SolanaWallet) {
        updateStatus('Slope extension not found. Please install it from https://slope.finance.', 'error');
        return;
      }

      const wallet = new window.slope.SolanaWallet();
      const resp = await wallet.connect();
      setUserAddress(resp.publicKey.toString());
      showVerificationSection();
    } catch (err) {
      updateStatus('Slope connection failed: ' + err.message, 'error');
    }
  };

  const connectGlow = async () => {
    try {
      if (typeof window.glow === 'undefined') {
        updateStatus('Glow extension not found. Please install it from https://glow.app.', 'error');
        return;
      }

      if (!window.glow.solana) {
        updateStatus('Glow extension not found. Please install it from https://glow.app.', 'error');
        return;
      }

      const resp = await window.glow.solana.connect();
      setUserAddress(resp.publicKey.toString());
      showVerificationSection();
    } catch (err) {
      updateStatus('Glow connection failed: ' + err.message, 'error');
    }
  };

  const connectClover = async () => {
    try {
      if (typeof window.clover_solana === 'undefined') {
        updateStatus('Clover extension not found. Please install it from https://clover.finance.', 'error');
        return;
      }

      const resp = await window.clover_solana.connect();
      setUserAddress(resp.publicKey.toString());
      showVerificationSection();
    } catch (err) {
      updateStatus('Clover connection failed: ' + err.message, 'error');
    }
  };

  const connectCoinbase = async () => {
    try {
      if (typeof window.coinbaseSolanaWallet === 'undefined') {
        updateStatus('Coinbase Wallet extension not found. Please install it from https://wallet.coinbase.com.', 'error');
        return;
      }

      const resp = await window.coinbaseSolanaWallet.connect();
      setUserAddress(resp.publicKey.toString());
      showVerificationSection();
    } catch (err) {
      updateStatus('Coinbase Wallet connection failed: ' + err.message, 'error');
    }
  };

  const connectExodus = async () => {
    try {
      if (typeof window.exodus === 'undefined') {
        updateStatus('Exodus extension not found. Please install it from https://exodus.com.', 'error');
        return;
      }

      if (!window.exodus.solana) {
        updateStatus('Exodus extension not found. Please install it from https://exodus.com.', 'error');
        return;
      }

      const resp = await window.exodus.solana.connect();
      setUserAddress(resp.publicKey.toString());
      showVerificationSection();
    } catch (err) {
      updateStatus('Exodus connection failed: ' + err.message, 'error');
    }
  };

  const connectBrave = async () => {
    try {
      if (typeof window.braveSolana === 'undefined') {
        updateStatus('Brave Wallet extension not found. Please install it from https://brave.com/wallet.', 'error');
        return;
      }

      const resp = await window.braveSolana.connect();
      setUserAddress(resp.publicKey.toString());
      showVerificationSection();
    } catch (err) {
      updateStatus('Brave Wallet connection failed: ' + err.message, 'error');
    }
  };

  const connectTorus = async () => {
    try {
      if (typeof window.torus === 'undefined') {
        updateStatus('Torus Wallet not found. Please visit https://toruswallet.io.', 'error');
        return;
      }

      const torus = new window.torus.Torus();
      await torus.init();
      const resp = await torus.login();
      setUserAddress(resp.publicKey.toString());
      showVerificationSection();
    } catch (err) {
      updateStatus('Torus Wallet connection failed: ' + err.message, 'error');
    }
  };

  const connectTrust = async () => {
    try {
      if (typeof window.trustwallet === 'undefined') {
        updateStatus('Trust Wallet extension not found. Please install it from https://trustwallet.com.', 'error');
        return;
      }

      if (!window.trustwallet.solana) {
        updateStatus('Trust Wallet extension not found. Please install it from https://trustwallet.com.', 'error');
        return;
      }

      const resp = await window.trustwallet.solana.connect();
      setUserAddress(resp.publicKey.toString());
      showVerificationSection();
    } catch (err) {
      updateStatus('Trust Wallet connection failed: ' + err.message, 'error');
    }
  };

  const showVerificationSection = () => {
    setShowVerification(true);
    updateStatus('Wallet connected! Click "Verify NFT Ownership" to continue.', 'success');
    fetchAndDisplayNFTs();
  };

  const fetchAndDisplayNFTs = async () => {
    if (!userAddress) return;
    updateStatus('Fetching your NFTs...', 'info');
    setNfts([]);
    setShowNFTs(true);

    try {
      const url = `${API_BASE_URL}/api/addresses/${userAddress}/nft-assets?api-key=${heliusApiKey}`;
      const res = await fetch(url);
      const nftData = await res.json();

      if (nftData && nftData.length > 0) {
        setNfts(nftData);
        updateStatus('NFTs loaded! Click "Verify NFT Ownership" to continue.', 'success');
      } else {
        setNfts([]);
        updateStatus('No NFTs found in your wallet.', 'error');
      }
    } catch (e) {
      setNfts([]);
      updateStatus('Failed to fetch NFTs: ' + e.message, 'error');
    }
  };

  const verifyNFT = async () => {
    if (!userAddress || !tgId) {
      updateStatus('Missing wallet address or Telegram ID!', 'error');
      return;
    }

    updateStatus('Verifying NFT ownership...', 'info');

    try {
      const response = await fetch(`${API_BASE_URL}/api/verify-nft`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          wallet_address: userAddress,
          tg_id: tgId
        })
      });

      const result = await response.json();

      if (result.has_nft) {
        updateStatus('✅ NFT verified! You now have access to the Telegram group.', 'success');
        setWelcomeMessage('Welcome to Meta Betties Private Key. You may Enter');
      } else {
        updateStatus('❌ No required NFT found in your wallet. Access denied.', 'error');
      }
    } catch (error) {
      updateStatus('Verification failed: ' + error.message, 'error');
    }
  };

  const getStatusClasses = () => {
    const baseClasses = 'mb-6 p-4 rounded-xl border transition-all duration-300';
    const iconClasses = 'w-2 h-2 rounded-full mr-3';
    
    if (status.type === 'success') {
      return {
        container: `${baseClasses} bg-green-500/20 border-green-400/30`,
        icon: `${iconClasses} bg-green-400`
      };
    } else if (status.type === 'error') {
      return {
        container: `${baseClasses} bg-red-500/20 border-red-400/30`,
        icon: `${iconClasses} bg-red-400`
      };
    } else {
      return {
        container: `${baseClasses} bg-blue-500/20 border-blue-400/30`,
        icon: `${iconClasses} bg-blue-400`
      };
    }
  };

  const statusClasses = getStatusClasses();

  return (
    <div className="min-h-screen gradient-bg bg-cover bg-center">
      {/* Background Pattern */}
      <div className="absolute inset-0 opacity-10">
        <div 
          className="absolute inset-0" 
          style={{
            backgroundImage: `url('data:image/svg+xml,%3Csvg width="60" height="60" viewBox="0 0 60 60" xmlns="http://www.w3.org/2000/svg"%3E%3Cg fill="none" fill-rule="evenodd"%3E%3Cg fill="%23ffffff" fill-opacity="0.1"%3E%3Ccircle cx="30" cy="30" r="2"/%3E%3C/g%3E%3C/g%3E%3C/svg%3E')`
          }}
        />
      </div>

      <div className="relative z-10 min-h-screen flex items-center justify-center p-4">
        <div className="w-full max-w-2xl">
          {/* Header */}
          <div className="text-center mb-8 animate-fade-in">
            <div className="inline-flex items-center justify-center w-16 h-16 bg-white/20 rounded-full mb-4">
              <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
              </svg>
            </div>
            <h1 className="text-4xl font-bold text-white mb-2">NFT Verification Portal</h1>
            <p className="text-white/80 text-lg">{welcomeMessage}</p>
          </div>

          {/* Main Container */}
          <div className="glass-effect rounded-2xl p-8 shadow-2xl animate-fade-in">
            {/* Status Message */}
            <div className={statusClasses.container}>
              <div className="flex items-center">
                <div className={statusClasses.icon + ' animate-pulse'} />
                <span className="text-white">{status.message}</span>
              </div>
            </div>

            {/* Wallet Connection Section */}
            {!showVerification && (
              <div className="space-y-4">
                <h3 className="text-xl font-semibold text-white mb-4">Choose Your Solana Wallet</h3>
                <div className="grid grid-cols-2 sm:grid-cols-3 gap-4">
                  <button 
                    onClick={connectPhantom} 
                    className="wallet-btn glass-effect rounded-xl p-4 text-white hover:bg-white/20 transition-all duration-300"
                  >
                    <div className="flex items-center space-x-3">
                      <div className="w-8 h-8 bg-purple-500 rounded-lg flex items-center justify-center">
                        <span className="text-lg">🟣</span>
                      </div>
                      <div className="text-left">
                        <div className="font-semibold">Phantom</div>
                        <div className="text-sm text-white/70">Solana</div>
                      </div>
                    </div>
                  </button>

                  <button 
                    onClick={connectSolflare} 
                    className="wallet-btn glass-effect rounded-xl p-4 text-white hover:bg-white/20 transition-all duration-300"
                  >
                    <div className="flex items-center space-x-3">
                      <div className="w-8 h-8 bg-orange-400 rounded-lg flex items-center justify-center">
                        <span className="text-lg">🟠</span>
                      </div>
                      <div className="text-left">
                        <div className="font-semibold">Solflare</div>
                        <div className="text-sm text-white/70">Solana</div>
                      </div>
                    </div>
                  </button>

                  <button 
                    onClick={connectBackpack} 
                    className="wallet-btn glass-effect rounded-xl p-4 text-white hover:bg-white/20 transition-all duration-300"
                  >
                    <div className="flex items-center space-x-3">
                      <div className="w-8 h-8 bg-blue-500 rounded-lg flex items-center justify-center">
                        <span className="text-lg">🔵</span>
                      </div>
                      <div className="text-left">
                        <div className="font-semibold">Backpack</div>
                        <div className="text-sm text-white/70">Solana</div>
                      </div>
                    </div>
                  </button>

                  <button 
                    onClick={connectSlope} 
                    className="wallet-btn glass-effect rounded-xl p-4 text-white hover:bg-white/20 transition-all duration-300"
                  >
                    <div className="flex items-center space-x-3">
                      <div className="w-8 h-8 bg-green-500 rounded-lg flex items-center justify-center">
                        <span className="text-lg">🟢</span>
                      </div>
                      <div className="text-left">
                        <div className="font-semibold">Slope</div>
                        <div className="text-sm text-white/70">Solana</div>
                      </div>
                    </div>
                  </button>

                  <button 
                    onClick={connectGlow} 
                    className="wallet-btn glass-effect rounded-xl p-4 text-white hover:bg-white/20 transition-all duration-300"
                  >
                    <div className="flex items-center space-x-3">
                      <div className="w-8 h-8 bg-yellow-500 rounded-lg flex items-center justify-center">
                        <span className="text-lg">🟡</span>
                      </div>
                      <div className="text-left">
                        <div className="font-semibold">Glow</div>
                        <div className="text-sm text-white/70">Solana</div>
                      </div>
                    </div>
                  </button>

                  <button 
                    onClick={connectClover} 
                    className="wallet-btn glass-effect rounded-xl p-4 text-white hover:bg-white/20 transition-all duration-300"
                  >
                    <div className="flex items-center space-x-3">
                      <div className="w-8 h-8 bg-red-500 rounded-lg flex items-center justify-center">
                        <span className="text-lg">🔴</span>
                      </div>
                      <div className="text-left">
                        <div className="font-semibold">Clover</div>
                        <div className="text-sm text-white/70">Solana</div>
                      </div>
                    </div>
                  </button>

                  <button 
                    onClick={connectCoinbase} 
                    className="wallet-btn glass-effect rounded-xl p-4 text-white hover:bg-white/20 transition-all duration-300"
                  >
                    <div className="flex items-center space-x-3">
                      <div className="w-8 h-8 bg-blue-600 rounded-lg flex items-center justify-center">
                        <span className="text-lg">💙</span>
                      </div>
                      <div className="text-left">
                        <div className="font-semibold">Coinbase</div>
                        <div className="text-sm text-white/70">Solana</div>
                      </div>
                    </div>
                  </button>

                  <button 
                    onClick={connectExodus} 
                    className="wallet-btn glass-effect rounded-xl p-4 text-white hover:bg-white/20 transition-all duration-300"
                  >
                    <div className="flex items-center space-x-3">
                      <div className="w-8 h-8 bg-indigo-500 rounded-lg flex items-center justify-center">
                        <span className="text-lg">💜</span>
                      </div>
                      <div className="text-left">
                        <div className="font-semibold">Exodus</div>
                        <div className="text-sm text-white/70">Solana</div>
                      </div>
                    </div>
                  </button>

                  <button 
                    onClick={connectBrave} 
                    className="wallet-btn glass-effect rounded-xl p-4 text-white hover:bg-white/20 transition-all duration-300"
                  >
                    <div className="flex items-center space-x-3">
                      <div className="w-8 h-8 bg-orange-600 rounded-lg flex items-center justify-center">
                        <span className="text-lg">🦁</span>
                      </div>
                      <div className="text-left">
                        <div className="font-semibold">Brave</div>
                        <div className="text-sm text-white/70">Solana</div>
                      </div>
                    </div>
                  </button>

                  <button 
                    onClick={connectTorus} 
                    className="wallet-btn glass-effect rounded-xl p-4 text-white hover:bg-white/20 transition-all duration-300"
                  >
                    <div className="flex items-center space-x-3">
                      <div className="w-8 h-8 bg-teal-500 rounded-lg flex items-center justify-center">
                        <span className="text-lg">🌀</span>
                      </div>
                      <div className="text-left">
                        <div className="font-semibold">Torus</div>
                        <div className="text-sm text-white/70">Solana</div>
                      </div>
                    </div>
                  </button>

                  <button 
                    onClick={connectTrust} 
                    className="wallet-btn glass-effect rounded-xl p-4 text-white hover:bg-white/20 transition-all duration-300"
                  >
                    <div className="flex items-center space-x-3">
                      <div className="w-8 h-8 bg-blue-700 rounded-lg flex items-center justify-center">
                        <span className="text-lg">🛡️</span>
                      </div>
                      <div className="text-left">
                        <div className="font-semibold">Trust Wallet</div>
                        <div className="text-sm text-white/70">Solana</div>
                      </div>
                    </div>
                  </button>
                </div>
              </div>
            )}

            {/* Verification Section */}
            {showVerification && (
              <div className="animate-slide-up">
                <div className="glass-effect rounded-xl p-6 mb-6">
                  <div className="flex items-center justify-between mb-4">
                    <h3 className="text-xl font-semibold text-white">Wallet Connected</h3>
                    <div className="flex items-center space-x-2">
                      <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse" />
                      <span className="text-green-400 text-sm font-medium">Connected</span>
                    </div>
                  </div>
                  <div className="bg-black/20 rounded-lg p-3 mb-4">
                    <div className="text-sm text-white/70 mb-1">Address</div>
                    <div className="font-mono text-white break-all">
                      {userAddress ? `${userAddress.substring(0, 6)}...${userAddress.substring(userAddress.length - 4)}` : ''}
                    </div>
                  </div>
                  <button 
                    onClick={verifyNFT}
                    className="w-full bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 text-white font-semibold py-3 px-6 rounded-xl transition-all duration-300 transform hover:scale-105 shadow-lg"
                  >
                    <div className="flex items-center justify-center space-x-2">
                      <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                      </svg>
                      <span>Verify NFT Ownership</span>
                    </div>
                  </button>
                </div>
              </div>
            )}

            {/* NFT Display Section */}
            {showNFTs && (
              <div className="animate-slide-up">
                <div className="glass-effect rounded-xl p-6">
                  <h3 className="text-xl font-semibold text-white mb-4 flex items-center">
                    <svg className="w-6 h-6 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
                    </svg>
                    Your NFT Collection
                  </h3>
                  <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
                    {nfts.length > 0 ? (
                      nfts.map((nft, index) => (
                        <div key={index} className="nft-card glass-effect rounded-xl p-3 text-center">
                          <img
                            src={nft.content?.links?.image || 'data:image/svg+xml;base64,'}
                            alt={nft.content?.metadata?.name || 'NFT'}
                            className="w-full h-24 object-cover rounded-lg mb-2"
                          />
                          <div className="font-semibold text-white text-sm mb-1 truncate">
                            {nft.content?.metadata?.name || 'Unnamed NFT'}
                          </div>
                          <div className="text-xs text-white/70 truncate">
                            {nft.grouping && nft.grouping.length > 0 ? nft.grouping[0].group_value : ''}
                          </div>
                        </div>
                      ))
                    ) : (
                      <div className="col-span-full text-center py-8">
                        <div className="w-16 h-16 bg-white/10 rounded-full flex items-center justify-center mx-auto mb-4">
                          <svg className="w-8 h-8 text-white/50" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M20 13V6a2 2 0 00-2-2H6a2 2 0 00-2 2v7m16 0v5a2 2 0 01-2 2H6a2 2 0 01-2-2v-5m16 0h-2.586a1 1 0 00-.707.293l-2.414 2.414a1 1 0 01-.707.293h-3.172a1 1 0 01-.707-.293l-2.414-2.414A1 1 0 006.586 13H4" />
                          </svg>
                        </div>
                        <div className="text-white/70">No NFTs found in your wallet.</div>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Footer */}
          <div className="text-center mt-8 text-white/60 text-sm">
            <p>Secure verification powered by blockchain technology</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
